import React, { Component } from 'react';
import { Header, Grid, Segment } from 'semantic-ui-react';
import GridImageOne from '../../img/GridImageOne.png';

const styles = {
  gridBackgroundImage: {
    backgroundImage: `url(${GridImageOne})`,
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    height: '700px',
    opacity: '0.8'
  },
  headerPadding: {
    paddingTop: '40px',
    color: 'white'
  },
  whiteText: {
    color: 'white',
    fontFamily: 'Cormorant',
    fontHeight: '16px'
  }
};

export class Bio extends Component {
  render() {
    return (
      <Grid padded='vertically' centered>
        <Grid.Row
          style={styles.gridBackgroundImage}
          stretched
          textAlign='justifed'
        >
          <Grid.Column width={10}>
            <Header style={styles.headerPadding} textAlign='center' as='h3'>
              Bio
            </Header>
            <Segment style={styles.whiteText} basic>
              Born in 1992 a lover of Linkin Park, it was only natural that I
              become a drummer. I have drummed for the likes of Bob Marley and
              Slipknot. You can find me chillin on the beach slappin these
              bongos daily.
            </Segment>
          </Grid.Column>
        </Grid.Row>

        <Grid.Row stretched textAlign='justifed'>
          <Grid.Column width={10}>
            <Segment basic>
              Born in 1992 a lover of Linkin Park, it was only natural that I
              become a drummer. I have drummed for the likes of Bob Marley and
              Slipknot. You can find me chillin on the beach slappin these
              bongos daily.
            </Segment>
          </Grid.Column>
        </Grid.Row>
      </Grid>
    );
  }
}

export default Bio;
