import React, { Component } from 'react';
import { Button, Icon, Header, Grid, Image } from 'semantic-ui-react';
import Links from './Links';
import HomePhoto from '../../img/HomePhoto.png';

const styles = {
  buttonPadding: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    padding: '10px 12px',
    cursor: 'pointer'
  },
  buttonSize: {
    padding: '14px 80px'
  },
  centeredButtons: {
    display: 'flex',
    alignItems: 'center'
  },
  buttonGroup: {
    border: '1px solid green'
  },
  gridRowHeight: {
    height: '200px'
  },
  homeImage: {
    height: '200px',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat'
  }
};

const src = `${HomePhoto}`;

export class Home extends Component {
  render() {
    return (
      <Grid verticalAlign='middle' centered>
        <Links />
        <Grid.Row style={styles.gridRowHeight} columns={3}>
          <Grid.Column width={4} floated='left'>
            <Image src={src} style={styles.homeImage} fluid circular />
          </Grid.Column>
        </Grid.Row>
      </Grid>
    );
  }
}

export default Home;
