import React, { Component } from 'react';
import { Button, Icon, Header, Grid, Image, Segment } from 'semantic-ui-react';

const src = 'https://i.imgur.com/VfFFPqR.jpg';
const src1 = 'https://i.imgur.com/VfFFPqR.jpg';

const styles = {
  gridBackgroundImage: {
    backgroundImage:
      'url(' +
      'https://images.pexels.com/photos/34153/pexels-photo.jpg?auto=compress&cs=tinysrgb&h=350' +
      ')',
    backgroundPosition: 'center',
    backgroundSize: 'cover',
    backgroundRepeat: 'no-repeat'
  },
  headerPadding: {
    paddingTop: '40px'
  }
};

export class Bio extends Component {
  render() {
    return (
      <Grid centered style={styles.backgroundImage}>
        <Grid.Row stretched textAlign='justifed'>
          <Grid.Column width={10}>
            <Header style={styles.headerpadding} textAlign='center' as='h3'>
              Bio
            </Header>
            <Segment basic>
              Born in 1992 a lover of Linkin Park, it was only natural that I
              become a drummer. I have drummed for the likes of Bob Marley and
              Slipknot. You can find me chillin on the beach slappin these
              bongos daily.
            </Segment>
          </Grid.Column>
        </Grid.Row>

        <Grid.Row stretched textAlign='justifed'>
          <Grid.Column width={10}>
            <Segment basic>
              Born in 1992 a lover of Linkin Park, it was only natural that I
              become a drummer. I have drummed for the likes of Bob Marley and
              Slipknot. You can find me chillin on the beach slappin these
              bongos daily.
            </Segment>
          </Grid.Column>
        </Grid.Row>
      </Grid>
    );
  }
}

export default Bio;
