import React, { Component } from 'react';
import { Header, Grid, Segment } from 'semantic-ui-react';
import GridImageOne from '../../img/GridImageOne.png';
import GridImageTwo from '../../img/GridImageTwo.png';

const styles = {
  gridBackgroundImageOne: {
    backgroundImage: `url(${GridImageOne})`,
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    height: '700px',
    opacity: '0.8'
  },
  gridBackgroundImageTwo: {
    backgroundImage: `url(${GridImageTwo})`,
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    height: '700px',
    opacity: '0.8'
  },
  headerStyle: {
    color: 'white',
    fontFamily: 'Cormorant',
    fontSize: '50px',
    fontWeight: 'bold'
  },
  topText: {
    color: 'white',
    fontFamily: 'Cormorant',
    fontHeight: '12px',
    fontSize: '20px',
    fontWeight: 'bold',
    lineHeight: '2'
  },
  bottomText: {
    color: 'white',
    fontFamily: 'Cormorant',
    fontHeight: '12px',
    fontSize: '20px',
    fontWeight: 'bold',
    lineHeight: '2',
    paddingTop: '50px'
  }
};

export class Bio extends Component {
  render() {
    return (
      <Grid padded='vertically' centered>
        <Grid.Row
          style={styles.gridBackgroundImageOne}
          stretched
          textAlign='justifed'
        >
          <Grid.Column width={10}>
            <Header style={styles.headerStyle} textAlign='center' as='h1'>
              Bio
            </Header>
            <Segment style={styles.topText} basic>
              Umar Mausoof is a Los Angeles based drummer and percussionist. He
              currently plays with CJ Soul, New Embassy, Ingredient 10, and
              Becktoria.
            </Segment>
          </Grid.Column>
        </Grid.Row>

        <Grid.Row
          style={styles.gridBackgroundImageTwo}
          stretched
          textAlign='justifed'
        >
          <Grid.Column width={10}>
            <Segment style={styles.bottomText} basic>
              Umar Mausoof is a Los Angeles based drummer and percussionist. He
              currently plays with CJ Soul, New Embassy, Ingredient 10, and
              Becktoria.
            </Segment>
          </Grid.Column>
        </Grid.Row>
      </Grid>
    );
  }
}

export default Bio;
