import React, { Component } from 'react';
import { Segment, Header, Grid, Image } from 'semantic-ui-react';
import Links from './Links';
import HomePhoto from '../../img/HomePhoto.png';

const styles = {
  buttonPadding: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    padding: '10px 12px',
    cursor: 'pointer'
  },
  buttonSize: {
    padding: '14px 80px'
  },
  centeredButtons: {
    display: 'flex',
    alignItems: 'center'
  },
  buttonGroup: {
    border: '1px solid green'
  },
  gridRowHeight: {
    height: '500px'
  },
  homeImage: {
    height: '200px',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat'
  }
};

const src = `${HomePhoto}`;

export class Home extends Component {
  render() {
    return (
      <Grid verticalAlign='middle' centered>
        <Links />
        <Grid.Row style={styles.gridRowHeight} columns={3}>
          <Grid.Column width={10} centered>
            <Image src={src} fluid />
          </Grid.Column>
        </Grid.Row>
        <Grid.Row columns={3}>
          <Grid.Column width={10} centered>
            <Header as='h1' textAlign='center'>
              Umar Mausoof
            </Header>
          </Grid.Column>
        </Grid.Row>
      </Grid>
    );
  }
}

export default Home;
